import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
    // State'ler
    const [characters, setCharacters] = useState([]); // API'den gelen karakterler
    const [loading, setLoading] = useState(true); // Yükleme durumu
    const [error, setError] = useState(null); // Hata durumu
    const [filter, setFilter] = useState(""); // İsim filtresi
    const [selectedCharacter, setSelectedCharacter] = useState(null); // Seçilen karakter

    // API'den verileri çekme
    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const response = await axios.get("https://rickandmortyapi.com/api/character");
                setCharacters(response.data.results); // Karakterleri kaydet
                setError(null); // Hata yoksa temizle
            } catch (err) {
                setError("Failed to fetch data. Please try again."); // Hata mesajı
            } finally {
                setLoading(false); // Yükleme durumunu kapat
            }
        };
        fetchData();
    }, []);

    // Filtrelenmiş karakterler
    const filteredCharacters = characters.filter((char) =>
        char.name.toLowerCase().includes(filter.toLowerCase()),
        char.status.toLowerCase().includes(filter.toLowerCase()),
        char.species.toLowerCase().includes(filter.toLowerCase())
    );

    return (
        <div className="App">
            <h1>Rick and Morty Characters</h1>

            {/* Filtreleme Alanı */}
            <input
                type="text"
                placeholder="Filter by name"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                style={{ marginBottom: "20px", padding: "8px", width: "300px" }}
            />

            {/* Hata Mesajı */}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {/* Yükleme Durumu */}
            {loading ? (
                <p>Loading...</p>
            ) : filteredCharacters.length > 0 ? (
                // Tablo
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Status</th>
                            <th>Species</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredCharacters.map((char) => (
                            <tr key={char.id} onClick={() => setSelectedCharacter(char)}>
                                <td>{char.name}</td>
                                <td>{char.status}</td>
                                <td>{char.species}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No characters found.</p>
            )}

            {/* Seçilen Karakter Detayları */}
            {selectedCharacter && (
                <div style={{ marginTop: "20px" }}>
                    <h2>Character Details</h2>
                    <p><strong>Name:</strong> {selectedCharacter.name}</p>
                    <p><strong>Status:</strong> {selectedCharacter.status}</p>
                    <p><strong>Species:</strong> {selectedCharacter.species}</p>
                    <p><strong>Gender:</strong> {selectedCharacter.gender}</p>
                </div>
            )}
        </div>
    );
};

export default App;
