import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "./components/Modal"; // Modal bileşenini import ettik
import "./App.css";

const App = () => {
    const [allCharacters, setAllCharacters] = useState([]); // Tüm karakterler
    const [currentPage, setCurrentPage] = useState(1); // Şu anki sayfa
    const [loading, setLoading] = useState(true); // Yükleme durumu
    const [error, setError] = useState(null); // Hata mesajı
    const [selectedCharacter, setSelectedCharacter] = useState(null); // Modal için seçilen karakter
    const [filter, setFilter] = useState(""); // Filtreleme için kullanılan giriş

    const charactersPerPage = 9; // Her sayfada gösterilecek karakter sayısı

    // Tüm karakterleri API'den çekme
    useEffect(() => {
        const fetchAllCharacters = async () => {
            try {
                setLoading(true);
                let allCharactersTemp = [];
                let page = 1;
                let totalPages = 1;

                // İlk sayfayı çek ve toplam sayfa sayısını öğren
                const response = await axios.get(`https://rickandmortyapi.com/api/character?page=${page}`);
                allCharactersTemp = response.data.results;
                totalPages = response.data.info.pages;

                // Kalan sayfaları çek
                const fetchPromises = [];
                for (let i = 2; i <= totalPages; i++) {
                    fetchPromises.push(axios.get(`https://rickandmortyapi.com/api/character?page=${i}`));
                }

                const results = await Promise.all(fetchPromises);
                results.forEach((res) => {
                    allCharactersTemp = [...allCharactersTemp, ...res.data.results];
                });

                setAllCharacters(allCharactersTemp); // Tüm karakterleri state'e kaydet
                setError(null);
            } catch (err) {
                setError("Failed to fetch characters. Please try again.");
            } finally {
                setLoading(false); // Yükleme durumunu kapat
            }
        };

        fetchAllCharacters();
    }, []);

    // Filtrelenmiş karakterler
    const filteredCharacters = allCharacters.filter((char) =>
        char.name.toLowerCase().includes(filter.toLowerCase())
    );

    // Şu anki sayfada gösterilecek karakterler
    const indexOfLastCharacter = currentPage * charactersPerPage;
    const indexOfFirstCharacter = indexOfLastCharacter - charactersPerPage;
    const currentCharacters = filteredCharacters.slice(indexOfFirstCharacter, indexOfLastCharacter);

    // Toplam sayfa sayısını hesaplama
    const totalPages = Math.ceil(filteredCharacters.length / charactersPerPage);

    // Sayfa değiştirici fonksiyonlar
    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage((prev) => prev + 1);
        }
    };

    const handlePreviousPage = () => {
        if (currentPage > 1) {
            setCurrentPage((prev) => prev - 1);
        }
    };

    return (
        <div>
            <h1>Rick and Morty Characters</h1>

            {/* Filtreleme Alanı */}
            <input
                type="text"
                placeholder="Filter by name"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                style={{ marginBottom: "20px", padding: "8px", width: "300px" }}
            />

            {/* Hata veya Yükleme Durumu */}
            {error && <p style={{ color: "red" }}>{error}</p>}
            {loading ? (
                <p>Loading...</p>
            ) : currentCharacters.length > 0 ? (
                <>
                    {/* Karakterler Tablosu */}
                    <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px" }}>
                        {currentCharacters.map((char) => (
                            <div
                                key={char.id}
                                style={{
                                    border: "1px solid #ddd",
                                    borderRadius: "8px",
                                    padding: "10px",
                                    textAlign: "center",
                                    cursor: "pointer",
                                }}
                                onClick={() => setSelectedCharacter(char)}
                            >
                                <h3>{char.name}</h3>
                                <p>Status: {char.status}</p>
                                <p>Species: {char.species}</p>
                                <img src={char.image} alt={char.name} style={{ width: "100px", height: "100px" }} />
                            </div>
                        ))}
                    </div>

                    {/* Sayfalama Kontrolleri */}
                    <div style={{ marginTop: "20px", display: "flex", justifyContent: "center", gap: "10px" }}>
                        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
                            Previous
                        </button>
                        <p>
                            Page {currentPage} of {totalPages}
                        </p>
                        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
                            Next
                        </button>
                    </div>
                </>
            ) : (
                <p>No characters found.</p>
            )}

            {/* Modal bileşenini kullan */}
            <Modal character={selectedCharacter} onClose={() => setSelectedCharacter(null)} />
        </div>
    );
};

export default App;
