import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "./components/Modal"; // Modal bileşeni eklendi
import "./App.css";

const App = () => {
    const [characters, setCharacters] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filter, setFilter] = useState("");
    const [selectedCharacter, setSelectedCharacter] = useState(null); // Seçilen karakter için state

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const response = await axios.get("https://rickandmortyapi.com/api/character");
                setCharacters(response.data.results);
                setError(null);
            } catch (err) {
                setError("Failed to fetch data. Please try again.");
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const filteredCharacters = characters.filter((char) =>
        char.name.toLowerCase().includes(filter.toLowerCase())||
        char.status.toLowerCase().includes(filter.toLowerCase())||
        char.species.toLowerCase().includes(filter.toLowerCase())
    );

    return (
        <div className="App">
            <h1>Rick and Morty Characters</h1>
            <input
                type="text"
                placeholder="Filter by name or status or species"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                style={{ marginBottom: "20px", padding: "8px", width: "300px" }}
            />

            {error && <p style={{ color: "red" }}>{error}</p>}
            {loading ? (
                <p>Loading...</p>
            ) : filteredCharacters.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Status</th>
                            <th>Species</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredCharacters.map((char) => (
                            <tr key={char.id} onClick={() => setSelectedCharacter(char)}>
                                <td>{char.name}</td>
                                <td>{char.status}</td>
                                <td>{char.species}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No characters found.</p>
            )}

            {/* Modal bileşenini kullan */}
            <Modal character={selectedCharacter} onClose={() => setSelectedCharacter(null)} />
        </div>
    );
};

export default App;
