import React from "react";
import "./Modal.css"; // Modal için özel CSS dosyası

const Modal = ({ character, onClose }) => {
    if (!character) return null; // Eğer karakter seçilmediyse hiçbir şey gösterme

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <button className="close-button" onClick={onClose}>
                    &times;
                </button>
                <h2>Character Details</h2>
                <p><strong>Name:</strong> {character.name}</p>
                <p><strong>Status:</strong> {character.status}</p>
                <p><strong>Species:</strong> {character.species}</p>
                <p><strong>Gender:</strong> {character.gender}</p>
                <p><strong>Location:</strong> {character.location.name}</p>
                <img src={character.image} alt={character.name} style={{ width: "200px", borderRadius: "8px" }} />
            </div>
        </div>
    );
};

export default Modal;
